          text: 'Doctor Appointment',
          day: 'Deb 5th at 2:30pm',
          remainder: true,
      },
      {
          id: 2,
          text: 'Meeting at School',
          day: 'Deb 6th at 1:30pm',
          remainder: true,
      },
      {
          id: 3,
          text: 'Food Shooping',
          day: 'Deb 5th at 2:30pm',
          remainder: false,
      }
  ])

  const deleteTask = (id) => {
    console.log('delete', id)
  }

  return (
    <div className="container">
      <Header/>
      <Tasks tasks = {tasks} onDelete={dleteTask}/>
    </div>

  );
}

export default App;
